import React, {Component} from 'react';
import {Button, Input, Menu} from "element-react";

export default class Body extends Component {
    constructor(props) {
        super(props);

        this.headerData = ["bookName","Author","Language","Published","Sales"];
        this.bodyData=[
            ['Tfd rod','J.F','English','1954-1955','150milion'],
            ['Td rod','E.A','English','1904-1965','250milion'],
            ['Tsc rod','S.F','English','1944-1987','550milion'],
            ['Tfg rod','J.O','English','1923-1951','190milion'],
            ['Txfe rod','U.P','English','1914-1947','230milion']
        ];

        this.state = {
            postId: null
        };
    }

    async componentDidMount() {
        // POST request using fetch with async/await
        const requestOptions = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
            // body: JSON.stringify({ title: 'React POST Request Example' })
        };
        console.log("get data from backend");
        const response = await fetch('http://localhost:8088/kafka/test', requestOptions);
        console.log("response: ", response);
        // const data = await response.json();
        const data = await response.text();

        console.log("data: ", data);

        this.setState({ postId: data.id });

    }

    handleSelect = (index) => {
        console.log(index);
    };

    handleIconClick = () => {
        console.log('handleIconClick', this.state.searchInput);
    };

    render() {
        return (
            <header className="main-header visible">
                <div>
                    <table className='tabel' border="2">
                        <thead className='theads' onClick={this.sort}>
                        <tr>
                            {
                                this.headerData.map((head,index)=>
                                    <th key={index}>{head}</th> )
                            }
                        </tr>
                        </thead>
                        <tbody>
                        {
                            this.bodyData.map((row,index)=>{
                                return (<tr key={index}>
                                        {
                                            row.map((cell,index)=>{
                                                return <td >{cell}</td>
                                            })
                                        }
                                    </tr>
                                )
                            })
                        }
                        </tbody>
                    </table>
                </div>
            </header>
        )
    }
}
